# Affiche la taille du tableau
def tailleTableau(tableau)
    return tableau.size
end

# Retourne le nombre d'handle avec un numero
def handleWithNumeric(tableau)
    countNumero = 0

    tableau.each do |element|
        # puts element
        if element =~ /[0-9]/
            countNumero = countNumero + 1
        end
    end

    return countNumero
end

# Retourne le nombre d'handle avec 'aude'
def handleWithAude(tableau)
    countNumero = 0

    tableau.each do |element|
        # puts element
        if element =~ /[aA][uU][dD][eE]/
        # if element =~ /aude/i #i for ignore case / case insensitive 
            countNumero = countNumero + 1
        end
    end

    return countNumero
end

# Retourne le nombre d'handle avec une majuscule apres le @
def handleWithMajAfter(tableau)
    countNumero = 0

    tableau.each do |element|
        # puts element
        if element =~ /@[A-Z]/
            countNumero = countNumero + 1
        end
    end

    return countNumero
end

# Retourne le nombre d'handle avec au moins une majuscule 
def handleWithAMaj(tableau)
    countNumero = 0

    tableau.each do |element|
        # puts element
        if element =~ /[A-Z]/
            countNumero = countNumero + 1
        end
    end

    return countNumero
end

# Retourne le nombre total de underscore 
def handleWithUnderScore(tableau)
    countNumero = 0

    tableau.each do |element|
        # puts element
        if element =~ /_/
            countNumero = countNumero + 1
        end
    end

    return countNumero
end

# Retourne le tableau dans l'ordre alphabetique 
def alphaSort(tableau)
    # tableauSorted = tableau.sort
    # Sort alphabétique case insensitive: comparaison des version downcase (sans majuscule)
    tableauSorted = tableau.sort do |a, b|
        a.downcase <=> b.downcase
    end

    return tableauSorted
end

def alphaLenght(tableau)
    # tableauSorted = tableau.sort
    tableauSorted = tableau.sort_by(&:length)    
    return tableauSorted
end

# The main function
def main()
    totalJournalistes = tailleTableau($handle)
    puts "Il y a #{totalJournalistes} journalistes"

    totalNumeric = handleWithNumeric($handle)
    puts "il y a #{totalNumeric} qui ont des numéros !"

    totalAude = handleWithAude($handle)
    puts "il y a #{totalAude} qui ont Aude dans leur nom !"

    totalMajAfter = handleWithMajAfter($handle)
    puts "il y a #{totalMajAfter} qui ont une majuscule après le '@' !"

    totalAMaj = handleWithAMaj($handle)
    puts "il y a #{totalAMaj} qui ont au moins une majuscule"

    totalUnderScore = handleWithUnderScore($handle)
    puts "il y a #{totalUnderScore} '_' parmis la totalité des pseudos"
    puts

    sortAlpha = alphaSort($handle)
    puts "Liste sort alphabétique: #{sortAlpha.join(', ')}"
    puts

    sortLength = alphaLenght($handle)
    puts "Liste des 50 premiers handle les plus courts: #{sortLength[0..49].join(', ')}"
    puts
    
    puts "Index de @epenser: #{$handle.index("@epenser")}"
end

main()